bestiary = [
  {"name": "Crawlid", "desc": "Timid scavenger that crawls through the caverns looking for food.", "note": "Dull and pathetic. Hardly worth killing.", "icon": "bestiary_crawler_s.png", "preview": "bestiary_crawler_f.png", "id": "CRAWLER"},
  {"name": "Vengefly", "desc": "Flying predator that pursues its prey relentlessly.", "note": "This fierce little hunter will bravely chase down creatures many times its size. Admirable! It is weak though, and I have devoured hundreds of them with little effort or care.", "icon": "bestiary_buzzer_s.png", "preview": "bestiary_buzzer_f.png", "id": "BUZZER"},
  {"name": "Vengefly King", "desc": "Patriarch of a vengefly colony. Swoops at intruders and cries out for reinforcements if threatened.", "note": "A filthy creature that is happy to chew on any old thing it finds discarded on the cavern floor. When it notices a threat it will annoyingly scream and shriek, so kill it swiftly.", "icon": "bestiary_big-buzzer_s.png", "preview": "bestiary_big-buzzer_f.png", "id": "BIGBUZZER"},
  {"name": "Gruzzer", "desc": "Simple-minded creature that haphazardly buzzes around, bumping into things.", "note": "They only seem dimly aware of their surroundings, making them easy prey to cut your fangs on. What is it that guides them on their paths? It almost seems like they search for ways to die.", "icon": "bestiary_bouncer_s.png", "preview": "bestiary_bouncer_f.png", "id": "BOUNCER"},
  {"name": "Gruz Mother", "desc": "Mature gruzzer that carries its young in its belly. Attacks by hurling its huge body around.", "note": "Bizarrely, this monster does not lay eggs but instead carries its young inside of its fat stomach. This strange practice seems to exhaust the creature, making it sleepy and vulnerable. Take advantage!", "icon": "bestiary_big-bouncer_s.png", "preview": "bestiary_big-bouncer_f.png", "id": "BIGFLY"},
  {"name": "Tiktik", "desc": "Uses its small, sharp claws to climb along walls and rooves.", "note": "Spends its time tap-tap-tapping its way through the roads and caverns near the surface of this kingdom. If you're patient, you can wait for it to come to you and pick it off.", "icon": "bestiary_climber_s.png", "preview": "bestiary_climber_f.png", "id": "CLIMBER"},
  {"name": "Aspid Hunter", "desc": "Bests its prey by spitting corrosive liquid. Often hunts in packs.", "note": "Cunning predators that will try to pick you off from a distance. Don't give them any space, just charge in and cut them down! You'll find they're not so cunning once they are dead.", "icon": "bestiary__0008_spitter_s.png", "preview": "bestiary__0009_spitter.png", "id": "SPITTER"},
  {"name": "Aspid Mother", "desc": "Carries the eggs of its young inside its belly. Can hatch and birth its young as needed to deal with intruders.", "note": "Shamelessly uses its own young as a weapon against its foes! Appalling, yes, but I do admire its resourcefulness. Good hunters do not always make good parents.", "icon": "bestiary_hatcher_s.png", "preview": "bestiary_hatcher_f.png", "id": "HATCHER"},
  {"name": "Aspid Hatchling", "desc": "Recently birthed, these young aspid instinctively swarm around intruders.", "note": "These hatchlings are born with an instinct to kill! They are still only infants though, and you can crush them between your fangs without thinking. Just don't let them overwhelm you with numbers.", "icon": "bestiary_hatcher_baby_s.png", "preview": "bestiary_hatcher_baby_f.png", "id": "HATCHLING"},
  {"name": "Goam", "desc": "Thickly armoured, it bursts from the ground to seize prey in its fangs.", "note": "These brutes seem to be impervious to most damage, so you can sharpen your weapons on their thick hide or just slash away at them for your own amusement. They barely seem to notice.", "icon": "bestiary_armoured-worm_s.png", "preview": "bestiary_armoured-worm_f.png", "id": "WORM"},
  {"name": "Wandering Husk", "desc": "The remains of a bug, animated by a strange force. Wanders the roads where it once lived.", "note": "These \"civilised\" bugs of Hallownest were weak in life and now they are equally weak in death. Send them back into the dirt they were born in!", "icon": "bestiary_bug_s.png", "preview": "bestiary_bug_f.png", "id": "ZOM_RUNNER"},
  {"name": "Husk Hornhead", "desc": "The remains of a bug, animated by a strange force. Uses its horn to attack any uninfected creature it finds.", "note": "These bugs have an arrogant air about them, even after dying. Overly proud of their long horns! I enjoy snapping them off.", "icon": "bestiary_hornhead_s.png", "preview": "bestiary_hornhead_f.png", "id": "ZOM_HORNHEAD"},
  {"name": "Leaping Husk", "desc": "The remains of a bug, animated by a strange force. It will instinctively leap at uninfected creatures to attack them.", "note": "The bugs of old Hallownest did not hunt their own food, they had it brought to them. That's why they were so weak. That's why their kingdom crumbled into dust and faded away!", "icon": "bestiary_leaper_s.png", "preview": "bestiary_leaper_f.png", "id": "ZOM_LEAPER"},
  {"name": "Husk Bully", "desc": "The remains of a bug, animated by a strange force. It aggressively attacks any uninfected creature it finds.", "note": "A thick orange mist fills these walking corpses. It has a sweet, sickly taste to it. I find it foul. After you kill these creatures, I suggest you do not eat them.", "icon": "bestiary_barger_s.png", "preview": "bestiary_barger_f.png", "id": "ZOM_BARGER"},
  {"name": "Husk Warrior", "desc": "The remains of a bug, animated by a strange force. Wields a nail and shell.", "note": "Softened by generations of luxury, these bugs lack claws or armour or venom. Those who wish to protect themselves are forced to fashion weapons from stone, ore or shell.", "icon": "bestiary_shell-shielder_s.png", "preview": "bestiary_shell-shielder_f.png", "id": "ZOM_SHIELD"},
  {"name": "Husk Guard", "desc": "The remains of a great Hallownest Crossguard, animated by a strange force. Instinct still drives it to guard its post against intruders.", "note": "Larger and stronger than their brothers, these bugs are still forced to steal their weapons from the remains of other creatures. They do not roam or hunt, forever standing guard and lashing out at anything that wanders near.", "icon": "bestiary_crossroads-guard_s.png", "preview": "bestiary_crossroads-guard_f.png", "id": "ZOM_GUARD"},
  {"name": "Entombed Husk", "desc": "Mummified remains, deformed and enraged by infection.", "note": "Inside these shambling corpses is a bright light that pierces any darkness. I peered inside that light once and saw... something within it shining back. Something terrible.", "icon": "bestiary__0006_grave-zombie_s.png", "preview": "bestiary__0007_grave-zombie.png", "id": "MUMMY"},
  {"name": "False Knight", "desc": "A maggot driven mad by a strange force. Inhabits a stolen armoured shell.", "note": "Weak creatures love to steal the strength of others. Their lives are brief and fearful, and they yearn to have the power to dominate those who have dominated them.", "icon": "bestiary_false-knight_s.png", "preview": "bestiary_false-knight_f.png", "id": "FALSEKNIGHT"},
  {"name": "Maggot", "desc": "Weakest members of the kingdom of Hallownest. Generally looked down upon and forced to do menial labour.", "note": "Consisting mainly of sweet fat, these helpless creatures make excellent provisions for long journeys. Best roasted over open flame. If they try to bargain for their life, just ignore them. They have nothing to offer.", "icon": "bestiary_prayer_slug_s.png", "preview": "bestiary_prayer_slug_f.png", "id": "PRAYER_SLUG"},
  {"name": "Menderbug", "desc": "Member of an elusive guild of bugs fixated on the maintenance of Hallownest's many signs and wayposts.", "note": "The bugs of Hallownest were obsessed with building things. What strange little minds they had.", "icon": "bestiary_menderbug_s.png", "preview": "bestiary_menderbug_f.png", "id": "MENDERBUG"},
  {"name": "Lifeseed", "desc": "Small scuttling seed filled with Lifeblood, which can be extracted and consumed for its healthful properties.", "note": "The bright liquid inside of these little fellows is delicious, and drinking it will make you feel stronger. I wonder how my own innards taste... would it be a heinous thing to try and drink some?", "icon": "bestiary_blue_blob.png", "preview": "bestiary_blue_blob_f.png", "id": "HEALTH_SCUTTLER"},
  {"name": "Baldur", "desc": "Rolls into a ball and propels itself at intruders who wander into its territory. Aggressive even when newly born.", "note": "Younger hunters delight in catching these creatures and rolling them around for sport. I encourage you to indulge yourself if the fancy strikes you.", "icon": "bestiary_roller_s.png", "preview": "bestiary_roller_f.png", "id": "ROLLER"},
  {"name": "Elder Baldur", "desc": "Bulky and covered in thick plating. Retracts into itself when attacked.", "note": "As they age, these creatures become too heavy and lumpy to roll around freely. Instead, they curl up and hide from the world. What do they think of in there, wrapped up inside of themselves? More carefree times perhaps.", "icon": "bestiary_blocker_s.png", "preview": "bestiary_blocker_f.png", "id": "BLOCKER"},
  {"name": "Mosscreep", "desc": "Weakling that covers itself in leaves to appear larger. Hides amongst the undergrowth.", "note": "I used to think these things were merely ambling plants. When I learned they were actually living creatures, I began to kill them on sight. This is the nature of the Hunt!", "icon": "bestiary__0040_moss-crawler_s.png", "preview": "bestiary__0041_moss-crawler.png", "id": "MOSS_WALKER"},
  {"name": "Mossfly", "desc": "Winged weakling that covers itself in leaves to appear larger. Hides amongst the undergrowth, fleeing if predators draw near.", "note": "They spend their whole lives hiding or fleeing. How sad. Or, perhaps that is the life they choose for themselves and they enjoy it? It's a strange thought, but not impossible.", "icon": "bestiary__0016_green-flyer_s.png", "preview": "bestiary__0017_green-flyer.png", "id": "MOSS_FLYER"},
  {"name": "Mosskin", "desc": "Plant-like bug that charges at foes when close.", "note": "They can be hard to spot amongst the foliage until you get too close and they suddenly come charging at you. Tread carefully.", "icon": "bestiary_mossman_runner_s.png", "preview": "bestiary_mossman_runner_f.png", "id": "MOSSMAN_RUNNER"},
  {"name": "Volatile Mosskin", "desc": "Plant-like bug swollen with infection. Releases deadly gas when threatened.", "note": "The gas that seeps from their growths smells invitingly sweet, but it will burn through shell and flesh if you get too close!", "icon": "bestiary_mossman_shaker_s.png", "preview": "bestiary_mossman_shaker_f.png", "id": "MOSSMAN_SHAKER"},
  {"name": "Fool Eater", "desc": "Carnivorous plant that snaps at any creature that comes close.", "note": "You really would have to be a fool to be tricked by a plant.", "icon": "bestiary_snap_turret_s.png", "preview": "bestiary_snap_turret_f.png", "id": "SNAPPER_TRAP"},
  {"name": "Squit", "desc": "Charges at prey with its needle-like proboscis.", "note": "When they lunge at you, don't panic. Stand your ground, and strike back as they come close.", "icon": "bestiary_mosquito_s.png", "preview": "bestiary_mosquito_f.png", "id": "MOSQUITO"},
  {"name": "Obble", "desc": "Fat, slow, flying bug. Frequently discharges the acidic liquid stored in its round body.", "note": "Does it realise what a nuisance it is? Drifting around, spilling its noxious juices everywhere it goes? If you see any, make sure to kill them.", "icon": "bestiary_blob_fly_s.png", "preview": "bestiary_blob_fly_f.png", "id": "BLOB_FLYER"},
  {"name": "Gulka", "desc": "Aggressive plant-life that retracts protectively when danger is near. Spits hard spikey balls when foes are at a distance.", "note": "Its leaves are dry and bland. The bud is unpleasantly sour, but oh! The venom in those balls it spits. Exquisite! If you have the time to spare, try drinking some and enjoy the effects.", "icon": "bestiary_spit_turret_s.png", "preview": "bestiary_spit_turret_f.png", "id": "PLANT_SHOOTER"},
  {"name": "Maskfly", "desc": "Harmless flying creature that favours serene environments. Usually travels in flocks.", "note": "They all keep lookout for danger and when one flies away the rest do as well. I wonder how it feels to have the protection of a pack?", "icon": "bestiary_fung_pigeon_flyer_s.png", "preview": "bestiary_fung_pigeon_flyer_f.png", "id": "PIGEON"},
  {"name": "Moss Charger", "desc": "Timid creature that frightens away intruders by covering itself in huge clumps of moss.", "note": "It's small, but it finds a lot of courage and strength once it covers itself in foliage. And I can understand the desire to camouflage oneself...", "icon": "bestiary_mega_moss_charger_s.png", "preview": "bestiary_moss_charger_f.png", "id": "MOSS_CHARGER"},
  {"name": "Massive Moss Charger", "desc": "Family of creatures that have learnt to bulk up with moss and live as one.", "note": "There is strength in numbers, yes, but these creatures never had much strength to begin with. Cut through their trickery!", "icon": "bestiary_mega_moss_charger_s.png", "preview": "bestiary_mega_moss_charger_f.png", "id": "MEGA_MOSS_CHARGER"},
  {"name": "Moss Knight", "desc": "Protector of the Greenpath, trained in the ways of the Nail and the Shell.", "note": "They hide as bush-like mounds and look soft, but beware these warriors! They are relentless in combat, fiercely guarding something hidden deep in the Greenpath.", "icon": "bestiary_moss_knight_s.png", "preview": "bestiary_moss_knight_f.png", "id": "MOSS_KNIGHT"},
  {"name": "Mossy Vagabond", "desc": "Lazy, portly Mosskin. Easily swayed by others.", "note": "Slow, fat and easily vanquished; common threads in those who forget their calling and forsake their duties.", "icon": "bestiary_moss_knight_fat_s.png", "preview": "bestiary_moss_knight_fat_f.png", "id": "MOSS_KNIGHT_FAT"},
  {"name": "Durandoo", "desc": "Simple walking creature encased in a hard shell. Often found wading in shallow pools of acidic water.", "note": "One of the few creatures that can survive in the rivers of harsh acidic water that flow around us. Where does this water come from? It bubbles and spits with a seething hatred of the living...", "icon": "bestiary__0036_acid-walker_s.png", "preview": "bestiary__0037_acid-walker.png", "id": "ACID_WALKER"},
  {"name": "Duranda", "desc": "Simple flying creature protected by a spikey shell.", "note": "Thinks itself fully protected and so is unconcerned by passers by. Attack its face, and teach it that nothing is ever safe.", "icon": "bestiary__0038_acid-flyer_s.png", "preview": "bestiary__0039_acid-flyer.png", "id": "ACID_FLYER"},
  {"name": "Aluba", "desc": "Passive glider that prefers to dwell near acid waters.", "note": "What careless a life led drifting above us all. If one ever comes too close to the ground though...", "icon": "bestiary_fung_lazy_flyer_s.png", "preview": "bestiary_fung_lazy_flyer_f.png", "id": "LAZY_FLYER"},
  {"name": "Charged Lumafly", "desc": "When threatened they cluster together, generating electricity.", "note": "They usually drift about peacefully, but sometimes they cluster together and spit out a sharp, crackling flash that will stun and burn even large beasts like myself. Watch closely for an opening and move past them quickly.", "icon": "bestiary_zap_bugs_s.png", "preview": "bestiary_zap_bugs_f.png", "id": "ZAP_BUG"},
  {"name": "Uoma", "desc": "Smallest Jelly form. Passive and unaware, but pulsing with a dangerous electric charge.", "note": "The spirit of a dead ancestor? The brain of a larger creature, allowed to drift free? An egg searching for a safe place to spawn? I have no idea what this is.", "icon": "bestiary__0024_jelly-mini_s.png", "preview": "bestiary__0025_jelly-mini.png", "id": "JELLY_CRAWLER"},
  {"name": "Ooma", "desc": "Passive outer being protecting an aggressive, explosive core.", "note": "Ghastly, eerie creature made out of thick liquid. You can peer right through them, and can't see where they keep their thoughts. I don't trust them at all. Keep away.", "icon": "bestiary_jellyfish_s.png", "preview": "bestiary_jellyfish_f.png", "id": "JELLYFISH"},
  {"name": "Uumuu", "desc": "Intelligent being that guards the inner chamber of the Teacher's Archives.", "note": "The canyon below us, the one thick with fog and crackling with strange energy... a Hunter can lose their senses down there. Be careful... strange and unnatural things lurk there.", "icon": "bestiary__0072_giant-jellyfish_s.png", "preview": "bestiary__0073_giant-jellyfish.png", "id": "MEGA_JELLYFISH"},
  {"name": "Ambloom", "desc": "Simple-minded crawling fungus. Its cap is covered in a layer of hardened spikes.", "note": "Lie on your belly, open your maw and let this tasty morsel stroll straight into your stomach.", "icon": "bestiary__0014_fung-crawler_s.png", "preview": "bestiary__0015_fung-crawler.png", "id": "FUNG_CRAWLER"},
  {"name": "Fungling", "desc": "Passive fungal balloon. Internal gasses cause it to float.", "note": "Just a child, it drifts about with closed eyes and an open mind. When I was young, my brothers and sisters and I would hunt each other in the nest. Now I hunt alone.", "icon": "bestiary__0032_fungoon-mini_s.png", "preview": "bestiary__0033_fungoon-mini.png", "id": "FUNGOON_BABY"},
  {"name": "Fungoon", "desc": "Fully grown fungal balloon. Spits built up internal gas at aggressors.", "note": "So round! I don't think I've seen a rounder creature than this. This world certainly hides some marvellous creatures. I must hunt them all down!", "icon": "bestiary__0030_fungoon_s.png", "preview": "bestiary__0031_fungoon.png", "id": "FUNGOON"},
  {"name": "Sporg", "desc": "Detects nearby movement and fires out explosive spores that drift towards their target.", "note": "If you're skillful, you can lead their spores back to them and watch them perish in a destruction of their own making. There is a lesson in that, I think.", "icon": "bestiary_mushroom_turret_s.png", "preview": "bestiary_mushroom_turret_f.png", "id": "MUSH_TURRET"},
  {"name": "Fungified Husk", "desc": "The remains of a bug, consumed by a fungus. Frequently releases clouds of thick, deadly gas.", "note": "The bugs of Hallownest are so pathetic that being infected by poisonous fungi actually increases their chances of survival! They really are absurd creatures.", "icon": "bestiary_fungus_zombie_02_s.png", "preview": "bestiary_fungus_zombie_02_f.png", "id": "FUNG_ZOMBIE"},
  {"name": "Shrumeling", "desc": "Young fungal creature. Spends most of its time in the ground but can uproot and flee if it senses danger.", "note": "Looks like an ordinary mushroom until you get close, then it pops out of the ground and starts scurrying around blindly. Very, very amusing.", "icon": "bestiary_mushroom_mini_s.png", "preview": "bestiary_mushroom_mini_f.png", "id": "MUSH_BABY"},
  {"name": "Shrumal Warrior", "desc": "Fungal creature that attacks intruders to protect its young.", "note": "Something queer in the air has caused these mushrooms to grow hearts, minds, and even faces! This means they now fear for their lives, like all other living creatures.", "icon": "bestiary_mushroom_roller_s.png", "preview": "bestiary_mushroom_roller_f.png", "id": "MUSH_ROLLER"},
  {"name": "Shrumal Ogre", "desc": "Huge fungal creature. Attacks by spitting corrosive venom and slamming its body into foes.", "note": "Are these hulking things beasts or are they mushrooms? I cut one in half and peered inside to find out, but could find no answers in its soft, stinking insides.", "icon": "bestiary_mushroom_brawler_s.png", "preview": "bestiary_mushroom_brawler_f.png", "id": "MUSH_BRAWLER"},
  {"name": "Mantis Youth", "desc": "Member of a proud tribe, fiercely protective of its territory. Launches stinging attacks with its needle sharp abdomen.", "note": "As it grows, it loses the ability to fly. Passage into adulthood is often celebrated, but as time goes by we realise what we have lost.", "icon": "bestiary_mantis_flyer_s.png", "preview": "bestiary_mantis_flyer_f.png", "id": "MANTIS_FLYER_CHILD"},
  {"name": "Mantis Warrior", "desc": "Member of a proud tribe, fiercely protective of its territory. Launches savage attacks with bladed forelimbs.", "note": "If you venture too deep into the fungal forests, these warriors will stand in your way. What is it they guard, deep at the bottom of their territory?", "icon": "bestiary_mantis_s.png", "preview": "bestiary_mantis_f.png", "id": "MANTIS"},
  {"name": "Mantis Lords", "desc": "Leaders of the Mantis tribe and its finest warriors. They bear thin nail-lances and attack with blinding speed.", "note": "The Mantis tribe and the bugs of old Hallownest had no love for each other. The Mantises outlived their rivals though, and their civilisation still stands.", "icon": "bestiary_mantis_lords_s.png", "preview": "bestiary_mantis_lords_f.png", "id": "MANTIS_LORD"},
  {"name": "Husk Sentry", "desc": "Animated remains of a Hallownest Sentry. Still retains some memory of its former task.", "note": "More skilled at combat than most bugs of Hallownest, they are nimble but lightly armoured. Keep the pressure on them.", "icon": "bestiary_ruins_zombie_s.png", "preview": "bestiary_ruins_zombie_f.png", "id": "SENTRY_1"},
  {"name": "Heavy Sentry", "desc": "Animated remains of a large Hallownest Sentry. One of few in Hallownest strong enough to wield a greatnail.", "note": "Surprisingly quick-witted for a corpse. It will try to catch you if you leap over it and can chase you if you flee. Enjoyable to hunt, but beware them in packs.", "icon": "bestiary_ruins_zombie_big_s.png", "preview": "bestiary_ruins_zombie_big_f.png", "id": "SENTRY_FAT"},
  {"name": "Winged Sentry", "desc": "Flying sentry of Hallownest. Patrols the heights of the vast cavern in which the city was built.", "note": "Dances through the air just out of reach until it spots an opening, then swoops in for a decisive strike. If you're confident, wait for it to come to you then counter-attack.", "icon": "bestiary_ruins_zombie_flier_s.png", "preview": "bestiary_ruins_zombie_flier_f.png", "id": "SENTRY_FLYING"},
  {"name": "Lance Sentry", "desc": "Winged Sentry of Hallownest equipped with a nail-lance. Charges at threats or throws its lance from a distance.", "note": "This devious foe will often keep its distance and try to pick you off with its thrown weapons. When it charges at you, leap up and strike it down.", "icon": "bestiary_ruins_zombie_lance_s.png", "preview": "bestiary_ruins_zombie_lance_f.png", "id": "SENTRY_FLYING_JAVELIN"},
  {"name": "Mistake", "desc": "Once a highly intelligent bug, deformed by the misuse of soul.", "note": "These fools meddled with the soul of other creatures and lost their shape. Now they skulk through the shadows of their Sanctum like slugs, hoping their old brothers will build them new bodies.", "icon": "bestiary__0022_mage-blob_s.png", "preview": "bestiary__0023_mage-blob.png", "id": "MAGEBLOB"},
  {"name": "Folly", "desc": "Once a highly intelligent bug, inflated by misuse of soul.", "note": "I've heard whispers that the secret these fools were searching for was immortality. To live forever... I've never heard of anything so obscene!", "icon": "bestiary__0020_mage-balloon_s.png", "preview": "bestiary__0021_mage-balloon.png", "id": "MAGE_BALLOON"},
  {"name": "Soul Twister", "desc": "Highly intelligent bug that uses arcane knowledge to twist soul into vicious spells.", "note": "I have heard others talk about 'soul' but I do not understand what it actually is. I do know that freshly killed meat tastes best - is that because the 'soul' still clings to it?", "icon": "bestiary_mage_s.png", "preview": "bestiary_mage_f.png", "id": "MAGE"},
  {"name": "Soul Warrior", "desc": "Learned bug, wielding a refined pinblade. Enhances its combat prowess with harvested soul.", "note": "Where these strange warriors go, whispers follow them. Not only are they skilled with a nail, they can also use some strange power to shift suddenly from place to place. Stay on guard.", "icon": "bestiary_mage_knight_s.png", "preview": "bestiary_mage_knight_f.png", "id": "MAGE_KNIGHT"},
  {"name": "Soul Master", "desc": "Head of the Soul Sanctum. Hoarded soul hoping to stave off Hallownest's affliction, but eventually became intoxicated by its power.", "note": "The bugs of Hallownest tried all kinds of tricks and rituals and prayers to rid themselves of the infection. But to no avail! Perhaps the infection came from somewhere deep inside of them that they could not escape.", "icon": "bestiary_mage_lord_s.png", "preview": "bestiary_mage_lord_f.png", "id": "MAGE_LORD"},
  {"name": "Husk Dandy", "desc": "Former member of Hallownest's high society. Limited offensive ability.", "note": "It seems the weakest, greediest members of Hallownest were also the ones who exerted the most influence. Truly a Kingdom that was always destined for ruin.", "icon": "bestiary_royal_zombie_point_s.png", "preview": "bestiary_royal_zombie_point_f.png", "id": "ZOM_ROYAL_1"},
  {"name": "Cowardly Husk", "desc": "Former member of Hallownest's high society. Although made aggressive by the force inside, old instincts still occasionally compel it to run from danger.", "note": "The fearful, cowardly nature of these Hallownest bugs persists even after death! It almost feels shameful to chase and cut them down.", "icon": "bestiary_royal_zombie_flat_s.png", "preview": "bestiary_royal_zombie_flat_f.png", "id": "ZOM_ROYAL_2"},
  {"name": "Gluttonous Husk", "desc": "Former member of Hallownest's high society. Uses its corpulent body as a weapon.", "note": "As these bugs became wealthier they hunted less and consumed more, eventually taking on a grotesque shape not unlike over-ripe fruit.", "icon": "bestiary_royal_zombie_fat_s.png", "preview": "bestiary_royal_zombie_fat_f.png", "id": "ZOM_ROYAL_FAT"},
  {"name": "Gorgeous Husk", "desc": "Corpulent Husk encrusted with a shining metal. Retains the instinct to hoard Geo.", "note": "There is beauty in many things. Reflections of light in water, the taste of freshly killed meat, the cry of a hatchling for its mother... I have never understood the lust for metals and stones though.", "icon": "bestiary_gold_royal_s.png", "preview": "bestiary_gold_royal_f.png", "id": "GORGEOUS_HUSK"},
  {"name": "Great Husk Sentry", "desc": "Animated remains of a Great Sentry, the most elite of the city's guards. Wields a greatnail and shell. Its powerful attacks cause heavy damage.", "note": "An extremely rare thing, a bug of Hallownest that actually poses a threat. Watch them carefully, as they can lash out quickly with devastating strikes.", "icon": "bestiary_shield_guard_s.png", "preview": "bestiary__ruin_shield_guard_f.png", "id": "GREAT_SHIELD_ZOMBIE"},
  {"name": "Watcher Knight", "desc": "Discarded shell of a Watcher Knight, re-animated by a swarm of infected flies.", "note": "When these silent warriors fall in battle, their bodies split open and strange bugs come flying out. I wonder, what will come flying out of me when I die? Will my hopes and fears drift away into the darkness?", "icon": "bestiary_black_knight_s.png", "preview": "bestiary_black_knight_f.png", "id": "BLACK_KNIGHT"},
  {"name": "The Collector", "desc": "Dark figure locked inside the Tower of Love. Preserves Hallownest's creatures in glass jars with particular attention given to Grubs.", "note": "A shadow that sometimes flits through the caverns, making strange noises to itself. I've never seen it clearly so I have no idea what type of creature it is.", "icon": "bestiary__0068_collector-_s.png", "preview": "bestiary__0069_collector.png", "id": "JAR_COLLECTOR"},
  {"name": "Belfly", "desc": "Thin wings hide a highly volatile belly. Will give its own life to protect its territory.", "note": "An angry creature with no regard for its own life. A creature without fear... can it truly be called \"prey\"?", "icon": "bestiary_bat_dropper_s.png", "preview": "bestiary_bat_dropper_f.png", "id": "CEILING_DROPPER"},
  {"name": "Pilflip", "desc": "Odd creature that adapts to changing situations by flipping over and using the limbs on the other side of its body.", "note": "Adaptability is an important survival skill. If a problem seems insurmountable, try approaching it from a different angle.", "icon": "bestiary_flipper_s.png", "preview": "bestiary_flipper_f.png", "id": "FLIP_HOPPER"},
  {"name": "Hwurmp", "desc": "Tiny and placid but swells to enormous size when threatened.", "note": "Extremely amusing creature.", "icon": "bestiary_inflater_s.png", "preview": "bestiary_inflater_f.png", "id": "INFLATOR"},
  {"name": "Bluggsac", "desc": "Placid but putrid creature. Holds rancid eggs within its gut.", "note": "Easily tracked down if you have a sense of smell. I strongly suggest you do not eat its eggs.", "icon": "bestiar_egg_sac_s.png", "preview": "bestiary_egg_sac.png", "id": "EGG_SAC"},
  {"name": "Dung Defender", "desc": "Skilled combatant living at the heart of the Waterways. Assails intruders with balls of compacted dung.", "note": "Fighting for 'honour', or for 'loyalty'... you may as well be fighting for dust. If you want to kill, do it for your own sake. That is the nature of a true Hunter.", "icon": "bestiary__0004_dung-defender_s.png", "preview": "bestiary__0005_dung-defender.png", "id": "DUNG_DEFENDER"},
  {"name": "White Defender", "desc": "Figure from a fondly remembered past. Reacts gallantly to defeat by becoming stronger.", "note": "\"The Champion's Call, the Knotted Grove, the Battle of the Blackwyrm... I remember it all. I will carry those glories with me always... until we meet again.\"<br>- Ogrim of the Five Knights", "icon": "bestiary_White_Defender_portrait.png", "preview": "bestiary_White_Defender_full.png", "id": "WHITE_DEFENDER"},
  {"name": "Flukefey", "desc": "Youngest of its species. Its body is composed of teeth, wings and stomach.", "note": "Seems to want to latch onto any living thing that comes by with its sharp little teeth. It will leave its cousins alone though, so it isn't completely mindless.", "icon": "bestiary__0054_fluke-fairy_s.png", "preview": "bestiary__0055_fluke-fairy.png", "id": "FLUKEFLY"},
  {"name": "Flukemon", "desc": "Worm-like creature that prefers damp, dark habitats.", "note": "Dangerous, unpredictable foes that infest the pipes below Hallownest's capital. Even when it seems you've killed them, you need to stay on guard.", "icon": "bestiary_flukeman_s.png", "preview": "bestiary_flukeman_f.png", "id": "FLUKEMAN"},
  {"name": "Flukemunga", "desc": "Aged fluke, grown fat on the rich refuse found in the pipes below the capital.", "note": "When predators are too successful the surplus food they eat becomes soft bulk wrapped around their bodies. Still, these creatures at least seem to make good use of their additional weight.", "icon": "bestiary_fat_fluke.png", "preview": "bestiary_fat_fluke_full.png", "id": "FAT_FLUKE"},
  {"name": "Flukemarm", "desc": "An insatiable breeder. Has populated the Waterways with its kind. Its insides double as a nest for young Flukefeys.", "note": "The desire to breed, to leave behind a memory of ourselves in the shape of a child... it seems to be etched deep into the heart of every living creature. I too have felt the pull of that base instinct.", "icon": "bestiary__0056_fluke-mother_s.png", "preview": "bestiary__0057_fluke-mother.png", "id": "FLUKE_MOTHER"},
  {"name": "Shardmite", "desc": "Feeds on crystal dust and uses crystallised spines to protect itself from danger.", "note": "I don't find this creature particularly noteable or interesting. It should learn to fly, or breathe flames, or sing hauntingly. Anything to justify its existence.", "icon": "bestiary__0018_mines-crawler_s.png", "preview": "bestiary__0019_mines-crawler.png", "id": "MINES_CRAWLER"},
  {"name": "Glimback", "desc": "Slow, hardy creature. The crystals that grow on its back provide extra protection from foes above.", "note": "Large and well-protected, but slow and docile. The bugs of Hallownest used to force these creatures to carry around their carts and machines.", "icon": "bestiary_mines_goomba_s.png", "preview": "bestiary_mines_goomba_f.png", "id": "CRYS_CRAWLER"},
  {"name": "Crystal Hunter", "desc": "Fires shards of fast-growing crystal at intruders.", "note": "Make their homes all the way up at the top of the world. Do they use the crystals willingly as weapons or are they merely hosts and the crystals parasites?", "icon": "bestiary_mines_flier_s.png", "preview": "bestiary_mines_flier_f.png", "id": "CRYS_FLYER"},
  {"name": "Crystal Crawler", "desc": "Discharges searing beams of light from the crystals on its back as it crawls around, seemingly unaware.", "note": "These creatures look weak, but sometimes burning pillars of heat will burst out from the crystals they carry on their backs. Be wary, and time your strikes well!", "icon": "bestiary_mines_climber_s.png", "preview": "bestiary_mines_climber_f.png", "id": "CRYS_LAZER_BUG"},
  {"name": "Husk Miner", "desc": "Remains of a bug drawn to the Crystal Peak for its precious crystal. Its claw-pick now doubles as a fierce weapon.", "note": "The bugs of Hallownest lusted after the strangest things in life. They would dig furtively through the dirt for stone, ore and in particular the crystals that grow at the top of the world.", "icon": "bestiary_zombie_miner_s.png", "preview": "bestiary_zombie_miner_f.png", "id": "ZOM_MINER"},
  {"name": "Crystallised Husk", "desc": "Remains of a bug who died amongst the crystals. Animated by a strange force, it attacks by firing beams of light from its crystallised arms.", "note": "There is some strange power hidden in the crystals that grow up there in the peaks. They gleam and glow in the darkness, a bright point of searing heat in each one. They sing too, if you listen. Very softly...", "icon": "bestiary_zombie_miner_crystal_s.png", "preview": "bestiary_zombie_miner_crystal_f.png", "id": "ZOM_BEAM_MINER"},
  {"name": "Crystal Guardian", "desc": "Heavyset miner of the Crystal Peak overcome by crystal growth. Can discharge blazing beams of light from its crystalllised limbs.", "note": "How does the world look to this creature, gazing out from within its crystal prison? Does it see only light? Is that what drives it into a frenzy?", "icon": "bestiary__0064_mega-beam-miner_s.png", "preview": "bestiary__0065_mega-beam-miner.png", "id": "MEGA_BEAM_MINER"},
  {"name": "Furious Vengefly", "desc": "Vengefly whose body has been distorted by continued infection. Highly aggressive.", "note": "The infection that passes from creature to creature grants strength and courage, but it also seems to enslave the will. Would you make such a bargain?", "icon": "bestiary_infected_buzzer_s.png", "preview": "bestiary_infected_buzzer_f.png", "id": "ANGRY_BUZZER"},
  {"name": "Volatile Gruzzer", "desc": "Gruzzer swollen with infection and primed to burst.", "note": "Horrible goo and noxious gas has started leaking down from above. Is there a source of all this pestilence? If so, I haven't seen it.", "icon": "bestiary_infected_fly_s.png", "preview": "bestiary_infected_fly_f.png", "id": "BURSTING_BOUNCER"},
  {"name": "Violent Husk", "desc": "Maddened by massive infection. Charges at enemies with no thought for its own preservation.", "note": "The bugs of Hallownest were twisted out of shape by that ancient sickness. First they fell into deep slumber, then they awoke with broken minds, and then their bodies started to deform...", "icon": "bestiary_infected_husk1_s.png", "preview": "bestiary_infected_husk1_f.png", "id": "ZOM_BURSTING"},
  {"name": "Slobbering Husk", "desc": "The body of a bug, completely consumed by infection. Spits corrosive liquid.", "note": "The infection that swept through Hallownest so long ago... they say that the harder you struggled against it, the more it consumed you.", "icon": "bestiary_infected_husk2_s.png", "preview": "bestiary_infected_husk2_f.png", "id": "ZOM_SPITTING"},
  {"name": "Dirtcarver", "desc": "Swarming predator of the Deepnest. Burrows through the ground and bursts out to surprise prey.", "note": "Springy and vicious, these hunters will try to overwhelm you with numbers in the dark, tight, twisting caverns they carve through the ground. Always be on your guard!", "icon": "bestiary_centipede_s.png", "preview": "bestiary_centipede_f.png", "id": "BABY_CENTIPEDE"},
  {"name": "Carver Hatcher", "desc": "Flying creature that fires out dirtcarvers from its body when potential prey comes close.", "note": "Are these creatures the mothers of the dirtcarvers? They appear only loosely related. But then how do the dirtcarvers get inside of the hatchers? A confusing situation.", "icon": "bestiary_centipede_hatcher_s.png", "preview": "bestiary_centipede_hatcher_f.png", "id": "CENTIPEDE_HATCHER"},
  {"name": "Garpede", "desc": "Giant burrower covered in thick shell plates. Tirelessly travels the tunnels of the Deepnest.", "note": "Monstrously strong, they shrug off any attacks you make on their thickly armoured bodies. Luckily, if you simply avoid their paths they'll leave you alone.", "icon": "bestiary_big_centipede_s.png", "preview": "bestiary_big_centipede_f.png", "id": "BIG_CENTIPEDE"},
  {"name": "Corpse Creeper", "desc": "Parasitic beast that controls the body of its host after the host's death.", "note": "The beasts of this land have found plenty of ingenious hiding places, but none so clever as the body of another creature! If you happen on such a beast, rejoice! You've hunted two creatures at once!", "icon": "bestiary_zombie_spider_02_s.png", "preview": "bestiary_zombie_spider_01_f.png", "id": "ZOM_SPIDER"},
  {"name": "Deepling", "desc": "Infant of the Deepnest. Scuttles about in the darkness, learning the layout of its home.", "note": "The Deepnest is a dangerous place to hunt, but rewarding. Keep an eye out for weaker creatures you can pick off and eat. Keep your strength up in the darkness.", "icon": "bestiary_tiny_spider_s.png", "preview": "bestiary_tiny_spider_f.png", "id": "MINI_SPIDER"},
  {"name": "Deephunter", "desc": "Crawls along walls and roofs, spitting burning venom at anything that passes by.", "note": "They can be hard to spot as they flitter in and out of the darkness. Watch for their glowing eyes.", "icon": "bestiary__0050_spit-spider_s.png", "preview": "bestiary__0051_spit-spider.png", "id": "SPITTING_SPIDER"},
  {"name": "Little Weaver", "desc": "Nimble spider that traverses its domain on thin strands of web. Often waits in ambush.", "note": "They can appear from the darkness at any moment! They seem to fly through the air, dancing on invisible strings as they silently stalk prey.", "icon": "bestiary__0010_spider-flyer_s.png", "preview": "bestiary__0011_spider-flyer.png", "id": "SPIDER_FLYER"},
  {"name": "Stalking Devout", "desc": "Worshippers of Herrah the Beast. Shield themselves with hard plated foreclaws, then lash out suddenly to cut down prey.", "note": "Powerful, well-armoured and fast, these are no mere beasts. Study their movements and watch carefully for an opening.", "icon": "bestiary_spider_soldier_s.png", "preview": "bestiary_spider_soldier_f.png", "id": "SLASH_SPIDER"},
  {"name": "Nosk", "desc": "Mimics the shape of other creatures to lure prey to its lair.", "note": "In the deepest darkness, there are beasts who wear faces stolen from your memories and pluck at the strings in your heart. Know yourself, and stay strong.", "icon": "bestiary__0058_mimic-spider_s.png", "preview": "bestiary__0059_mimic-spider.png", "id": "MIMIC_SPIDER"},
  {"name": "Shadow Creeper", "desc": "Found only in deep, dark places. Has never been observed to eat or drink anything.", "note": "This little creeper is weak and poses no threat, but something about it still unsettles me. The way it moves and the noises it makes are... strange somehow.", "icon": "bestiary__0046_abyss-crawler_s.png", "preview": "bestiary__0047_abyss-crawler.png", "id": "ABYSS_CRAWLER"},
  {"name": "Lesser Mawlek", "desc": "Attacks by spitting acid and slashing with foreclaws.", "note": "If you try to keep your distance it will spit sticky, burning globs of vile stomach juice. Move in quickly and strike decisively.", "icon": "bestiary_mini_mawlek_s.png", "preview": "bestiary_mini_mawlek_f.png", "id": "LESSER_MAWLEK"},
  {"name": "Mawlurk", "desc": "Large, immobile creature. Wildly spits venom when intruders pass by.", "note": "How did this foolish creature lose its legs?! I suppose it's possible to lose anything, if you really try.", "icon": "bestiary_mawlek_turret_s.png", "preview": "bestiary_mawlek_turret_f.png", "id": "MAWLEK_TURRET"},
  {"name": "Brooding Mawlek", "desc": "Ferocious but extremely social creature. Becomes aggressive if not able to mingle with its own kind.", "note": "I hear this beast crying out sometimes as I prowl the caverns, although I've never actually laid eyes on it. Who or what is it calling for? As far as I can tell, its voice is never answered.", "icon": "bestiary_mawlek_s.png", "preview": "bestiary_mawlek_f.png", "id": "MAWLEK"},
  {"name": "Lightseed", "desc": "A single-celled organism, completely infected. Scurries about simple-mindedly.", "note": "Strange air has been seeping down from above for years. Some of the air became liquid, and some of that liquid became flesh, and some of that flesh came to life. I don't know what to make of it.", "icon": "bestiary__0012_orange-blob_s.png", "preview": "bestiary__0013_orange-blob.png", "id": "PARASITE_BLOB"},
  {"name": "Infected Balloon", "desc": "Single cell of infection. Has a simple mind and can float using internal gasses.", "note": "The infection creeping through the Kingdom's dead veins... does it have a mind? Does it produce soul? Is it truly alive?", "icon": "bestiary_parasite_flyer_s.png", "preview": "bestiary_parasite_flyer_f.png", "id": "ORANGE_BALLOON"},
  {"name": "Broken Vessel", "desc": "Shattered corpse, reanimated by infected parasites.", "note": "The shape of this creature... I have seen something like it before. More than once, perhaps. It looks a little like the bugs of Hallownest, but not quite the same. Where did these empty little wanderers come from?", "icon": "bestiary_infected_knight_s.png", "preview": "bestiary_infected_knight_f.png", "id": "INFECTED_KNIGHT"},
  {"name": "Boofly", "desc": "Large, docile creature that peacefully buzzes around in the caverns of Kingdom's Edge.", "note": "Too slow to escape you and too large to hide from you. This prey will not challenge you, but sometimes it is good to take things easy.", "icon": "bestiary__0052_blow-fly_s.png", "preview": "bestiary__0053_blow-fly.png", "id": "BIG_FAT_FLYER"},
  {"name": "Primal Aspid", "desc": "Wilder, ancestral form of the Aspid. Once thought extinct, they have reappeared at the edges of the world.", "note": "These cruel foes will ambush you and relentlessly attack with their searing venom. How strange that their descendants are so weak. If I have children, I hope they will be stronger than me...", "icon": "bestiary_super_spitter_s.png", "preview": "bestiary_super_spitter_f.png", "id": "SUPER_SPITTER"},
  {"name": "Hopper", "desc": "Leaps at prey and uses its sharp proboscis to drain vital fluids.", "note": "This hopping thief's belly is full of the blood it has stolen from other beasts. If you hold it above a fire, the belly will grow and grow until it explodes in a steaming shower of fluids.", "icon": "bestiary_hopper_s.png", "preview": "bestiary_hopper_f.png", "id": "HOPPER"},
  {"name": "Great Hopper", "desc": "Leaps around the edge of the world, trying to crush prey beneath its body.", "note": "A worthy foe, surprisngly nimble for its size. Either keep your distance, or try to dance between its hops and strike at its legs.", "icon": "bestiary__0070_giant-hopper_s.png", "preview": "bestiary__0071_giant-hopper.png", "id": "GIANT_HOPPER"},
  {"name": "Grub Mimic", "desc": "Assumes the shape of a harmless creature to lure prey.", "note": "Luring an enemy close by pretending to be a weak little grub. Very cunning! Unfortunately for them, I eat these grubs as soon as I see them.", "icon": "bestiary_mimic_grub_s.png", "preview": "bestiary_mimic_grub_f.png", "id": "GRUB_MIMIC"},
  {"name": "Hiveling", "desc": "Smallest of the Hive's inhabitants. Carries out simple tasks.", "note": "Weak, but hard-working and numerous. Their hairy little bodies are filled with a thick, sweet, golden liquid. Good to keep a few on you for long hunts.", "icon": "bestiary__bee_tiny_s.png", "preview": "bestiary_bee_tiny.png", "id": "BEE_HATCHLING"},
  {"name": "Hive Soldier", "desc": "Defends the Hive against intruders with its extendable stinger.", "note": "Savage and dedicated warriors. Be prepared to fight them to the death if you plan on invading their home.", "icon": "bestiary_bee_s.png", "preview": "bestiary_bee_f.png", "id": "BEE"},
  {"name": "Hive Guardian", "desc": "Protects the Hive by hurling its huge body at intruders.", "note": "Like their smaller kin, their only instinct is to protect the Hive at any cost to themselves. Somewhat clumsy, they'll often damage the structure of the Hive when charging at intruders. Does this cause them shame? I do not know whether they have the capacity for such a thing.", "icon": "bestiary_bee_big_s.png", "preview": "bestiary_bee_big_f.png", "id": "BIG_BEE"},
  {"name": "Husk Hive", "desc": "Cowardly husk, its body colonised by hivelings.", "note": "Did the hivelings build their nest around this sorry bug, or did the bug squeeze its body into their nest? Either way, they seem happy enough together.", "icon": "bestiary_zombie_hive_s.png", "preview": "bestiary_zombie_hive_f.png", "id": "ZOM_HIVE"},
  {"name": "Hive Knight", "desc": "Loyal protector of the Hive's Queen. Flightless but ferocious in battle.", "note": "Some creatures are born with duties and loyalties marked indelibly upon their minds. They are strong, but they are also slaves.", "icon": "bestiary_hive_knight_s.png", "preview": "bestiary_hive_knight_f.png", "id": "HIVE_KNIGHT"},
  {"name": "Spiny Husk", "desc": "Overgrown with vegetation, it attacks by firing sharp spines from its body.", "note": "The spines they fire seem to be mildly poisonous. Don't use them to clean between your fangs after eating!", "icon": "bestiary_fungus_zombie_03_s.png", "preview": "bestiary_fungus_zombie_03_f.png", "id": "ZOM_SPINE"},
  {"name": "Loodle", "desc": "Inhabits the wilds beside the Queen's Gardens. Not aggressive in nature, but its wild bouncing behaviour can be dangerous.", "note": "It seems like their energy is limitless! Do they ever stop to sleep, or eat, or love?", "icon": "bestiary__0028_crazy-hopper_s.png", "preview": "bestiary__0029_crazy-hopper.png", "id": "GRASS_HOPPER"},
  {"name": "Mantis Petra", "desc": "Lurks amongst foliage and attacks by flinging wind-scythes from a distance.", "note": "They will harass you from afar, so get close and strike quickly. Their bodies are brittle and will easily fall to your nail.", "icon": "bestiary_heavy_mantis_flyer_s.png", "preview": "bestiary_heavy_mantis_flyer_f.png", "id": "MANTIS_FLYER_ADULT"},
  {"name": "Mantis Traitor", "desc": "Once a member of the Mantis tribe, now cast out and driven mad by infection.", "note": "Belonging to a tribe, or not belonging. I don't really understand what the difference is, or how it works. I have no tribe of my own.", "icon": "bestiary_heavy_mantis_s.png", "preview": "bestiary_heavy_mantis_f.png", "id": "MANTIS_HEAVY"},
  {"name": "Traitor Lord", "desc": "Deposed Lord of the Mantis tribe. Embraced the infection and turned against his sisters.", "note": "I have felt that desire. The desire to take the infection into myself. To become stronger, more powerful... these thoughts haunt my dreams during the darkest times. A false hope, but it can burn so brilliantly in one's mind.", "icon": "bestiary__0060_traitor-lord_s.png", "preview": "bestiary__0061_traitor-lord.png", "id": "TRAITOR_LORD"},
  {"name": "Sharp Baldur", "desc": "Hardened baldur, bred for battle in the Colosseum of Fools. Tougher and more aggressive than its wild ancestor.", "note": "These creatures... I have never seen one fully grown. I suppose they aren't allowed to live long enough to become adults.", "icon": "bestiary_colosseum_armoured_roller_s.png", "preview": "bestiary_colosseum_armoured_roller_f.png", "id": "COL_ROLLER"},
  {"name": "Armoured Squit", "desc": "Hardened squit, bred for battle in the Colosseum of Fools. Tougher and more aggressive than its wild ancestor.", "note": "Strong and quick! When they rear back, either move out of the way or prepare to strike back!", "icon": "bestiary_colosseum_armoured_mosquito_s.png", "preview": "bestiary_colosseum_armoured_mosquito_f.png", "id": "COL_MOSQUITO"},
  {"name": "Battle Obble", "desc": "Raised in the Colosseum. Although trained for combat, its movements and attacks are still haphazard.", "note": "They have crafted a tiny mask for it! Is such an extravagance practical I wonder?", "icon": "bestiary__0034_blobble_s.png", "preview": "bestiary__0035_blobble.png", "id": "BLOBBLE"},
  {"name": "Oblobble", "desc": "Larger cousin of the Obble. Pairs with a mate for life and will never leave its side.", "note": "Having a mate by your side... one that will never leave you until death. Strange. No mate has ever come forth to stay by my side...", "icon": "bestiary__0062_giant-fat-bee_s.png", "preview": "bestiary__0063_giant-fat-bee.png", "id": "OBLOBBLE"},
  {"name": "Shielded Fool", "desc": "Warrior fighting for glory in the Colosseum of Fools. Warily approaches opponents with shell held high.", "note": "That 'Colosseum' is a strange place where some bugs fight each other to the death and others watch. I don't understand such obscure rituals. If you venture in, stay on guard at all times.", "icon": "bestiary_colosseum_shield_gladiator_s.png", "preview": "bestiary_colosseum_shield_gladiator_f.png", "id": "COL_SHIELD"},
  {"name": "Sturdy Fool", "desc": "Warrior fighting for glory in the Colosseum of Fools. Uses crescent-shaped blades to cut down foes.", "note": "You can hit this warrior's spinning weapons out of the air, straight back into them! Useful if they manage to corner you.", "icon": "bestiary_colosseum_miner_s.png", "preview": "bestiary_colosseum_miner.png", "id": "COL_MINER"},
  {"name": "Winged Fool", "desc": "Warrior fighting for glory in the Colosseum of Fools. Flies just out of an opponent's reach then darts in for the kill.", "note": "What do they hope for, these warriors fighting in the Colosseum? Are they slaves, or prisoners, or do they freely choose to fight?", "icon": "bestiary_colosseum_flying_gladiator.png", "preview": "bestiary_colosseum_flying_gladiator_f.png", "id": "COL_FLYINGSENTRY"},
  {"name": "Heavy Fool", "desc": "Warrior fighting for glory in the Colosseum of Fools. Aggressively charges down foes.", "note": "Large, bulky, and fighting for its life. Don't underestimate this fighter! It can slither around quickly despite its bulk. It also has a surprisingly beautiful face beneath its mask.", "icon": "bestiary_colosseum_worm_gladiator_s.png", "preview": "bestiary_colosseum_worm_f.png", "id": "COL_WORM"},
  {"name": "Death Loodle", "desc": "Loodle equipped with a helmet and taught to kill.", "note": "They seem fierce, but they have a lot of difficulty aiming at their target. Watch their motions and strike when the opportunity comes. Take their helmet with you as a trophy.", "icon": "bestiary_colosseum_hopper_s.png", "preview": "bestiary_colosseum_hopper_f.png", "id": "COL_HOPPER"},
  {"name": "Volt Twister", "desc": "Learned bug that adapted the teachings of the Soul Sanctum into a deadly fighting art. Attacks with bursts of electricity.", "note": "Supposedly lives on a diet of lumaflies and spends its entire life fighting in the arena or meditating in seclusion. An odd foe.", "icon": "bestiary_electric_mage_s.png", "preview": "bestiary_electric_mage_f.png", "id": "ELEC_MAGE"},
  {"name": "God Tamer", "desc": "Veteran warrior of the Colosseum of Fools. Fights by the side of creatures she has trained.", "note": "Some hunters train beasts to join them in chasing down prey. I tried it once, but couldn't resist the urge to test my companion's strength against mine.", "icon": "bestiary__0066_lobster-lancer_s.png", "preview": "bestiary__0067_lobster-lancer.png", "id": "LOBSTER_LANCER"},
  {"name": "Pale Lurker", "desc": "Forgotten champion of the Colosseum, drawn away by strange obsessions.", "note": "This odd creature is unaffected by and unaware of the disease drifting through the caverns. Its madness is all its own.", "icon": "bestiary_gg__0000_icon_pale_runaway.png", "preview": "bestiary_gg__0009_pale_runaway.png", "id": "PALE_LURKER"},
  {"name": "Zote", "desc": "A self-proclaimed Knight, of no renown. Wields a nail he carved from shellwood, named 'Life Ender.'", "note": "Some rare creatures are so weak, so helpless, so inept and so irritating that hunting them gives no pleasure.", "icon": "bestiary__0048_zote_s.png", "preview": "bestiary__0049_zote.png", "id": "ZOTE"},
  {"name": "Grey Prince Zote", "desc": "Figment of an obsessed mind. Lacks grace but becomes stronger with every defeat.", "note": "\"My love, any creature who could bear to be away from you, who would willingly leave you behind or say unkind things to you... Pah! Lowly maggots, not worthy of standing in your glorious shadow!\"<br>She felt her cold body grow warm, a sensation she had almost forgotten...<br>- 'The Grey Prince' Chapter 112", "icon": "bestiary_mighty_zote_portrait.png", "preview": "bestiary_mighty_zote_full.png", "id": "GREY_PRINCE"},
  {"name": "Winged Zoteling", "desc": "Figment of an obsessed mind. Grudgingly assists its master in combat by lazily flying towards foes.", "note": "She asked whether he had bought the Charm for her as he promised he would, but then he explained. The Charm was actually of low quality and not worth the price asked. She understood the wisdom of his decision perfectly.<br>- 'The Grey Prince' Chapter 136", "icon": "bestiary_mighty_zoteling_portrait.png", "preview": "bestiary_mighty_zoteling_full.png", "id": "ZOTE_FLYER"},
  {"name": "Hopping Zoteling", "desc": "Figment of an obsessed mind. Jumps about in a nonthreatening manner.", "note": "\"That lowly map-maker's wife? Hah! My Queen, how could you compare yourself to her? In the face of your intoxicating beauty, all other females are merely dust!\"<br>The Grey Prince trembled with anger and indignation... and love.<br>- 'The Grey Prince' Chapter 178", "icon": "bestiary_mighty_zote_hopper_portrait.png", "preview": "bestiary_mighty_zote_hopper_full.png", "id": "ZOTE_HOPPER"},
  {"name": "Volatile Zoteling", "desc": "Figment of an obsessed mind. Deals poorly with the pressure of existing and quickly explodes.", "note": "The Grey Prince was a wanderer by nature, but she could not bear to be separated from him. So she devised a plan to make sure that she and her prince would be together always. She only had to wait for her chance.<br>- 'The Grey Prince' Chapter 205", "icon": "bestiary_mighty_zote_balloon_portrait.png", "preview": "bestiary_mighty_zote_balloon_full.png", "id": "ZOTE_BALLOON"},
  {"name": "Xero", "desc": "Lingering dream of a fallen warrior. Executed for crimes against the King.", "note": "A life defined by tragedy and triumph. A death marred by sorrow and regret. A spirit burdened by sins and memories. Better to wander the world than be cursed with glory.<br>- Xero", "icon": "bestiary_0006_ghost_xero.png", "preview": "bestiary_ghosts__0001_xero.png", "id": "GHOST_XERO"},
  {"name": "Gorb", "desc": "Lingering dream of a fallen warrior. A mysterious life-form that claimed to contain all of the world's knowledge inside of its distended brain.", "note": "I am Gorb!<br>- Gorb", "icon": "bestiary_0000_ghost_brainslug.png", "preview": "bestiary_ghosts__0000_brain-slug.png", "id": "GHOST_ALADAR"},
  {"name": "Elder Hu", "desc": "Lingering dream of a fallen warrior. Travelled the world tending to those affected by the plague.", "note": "Never forget what once was lost, yet do not let it tie you down.<br>- Elder Hu", "icon": "bestiary_0001_ghost_elderhu.png", "preview": "bestiary_ghosts__0002_elderhu.png", "id": "GHOST_HU"},
  {"name": "Marmu", "desc": "Lingering dream of a fallen warrior. Protector of the Queen's Gardens.", "note": "If you believe in something as hard as you can, with your eyes shut tight... eventually it will come true!<br>- Marmu", "icon": "bestiary_0004_ghost_caterpillar.png", "preview": "bestiary_ghosts__0005_marmu.png", "id": "GHOST_MARMU"},
  {"name": "No Eyes", "desc": "Lingering dream of a fallen warrior. Disappeared during the plague.", "note": "They're coming...<br>What's inside of you...<br>Strip it out!<br>- No Eyes", "icon": "bestiary_0005_ghost_no-eyes.png", "preview": "bestiary_ghosts__0006_no-eyes.png", "id": "GHOST_NOEYES"},
  {"name": "Galien", "desc": "Lingering dream of a fallen warrior. Trained in the wilds bordering the kingdom, hoping to become a Knight.", "note": "A Knight strives not only to protect the land and its people, but the hopes and dreams of the kingdom.<br>- Galien", "icon": "bestiary_0002_ghost_galien.png", "preview": "bestiary_ghosts__0003_galien.png", "id": "GHOST_GALIEN"},
  {"name": "Markoth", "desc": "Lingering dream of a fallen warrior. Only member of his forgotten tribe to take up a weapon.", "note": "May your efforts lead you somewhere worthy.<br>- Markoth", "icon": "bestiary_0003_ghost_markoth.png", "preview": "bestiary_ghosts__0004_markoth.png", "id": "GHOST_MARKOTH"},
  {"name": "Grimmkin Novice", "desc": "Young and playful member of the Grimm Troupe. As part of the Ritual, it gathers scarlet flame within its torch. It will relinquish the flame once defeated.", "note": "\"Shadows dream of endless fire,<br>Flames devour and embers swoop,<br>One will light the Nightmare Lantern,<br>Call and serve in Grimm's dread Troupe.\"<br>- 'The Grimm Troupe'", "icon": "bestiary__0004_grimmkin_little_portrait.png", "preview": "Bestiary_Grimmkin_little.png", "id": "FLAMEBEARER_SMALL"},
  {"name": "Grimmkin Master", "desc": "Powerful and devoted member of the Grimm Troupe. As part of the Ritual, it gathers scarlet flame within its torch. It will relinquish the flame once defeated.", "note": "\"A spark of red lights darkest dream,<br>Scarlet nightmares bright and wild,<br>Visions dance and flames do speak,<br>Burn the father, feed the child.\"<br>- 'The Grimm Troupe'", "icon": "bestiary__0003_grimmkin_fat_portrait.png", "preview": "Bestiary_Grimmkin_fat.png", "id": "FLAMEBEARER_MED"},
  {"name": "Grimmkin Nightmare", "desc": "Terrifying member of the Grimm Troupe. As part of the Ritual, it gathers scarlet flame within its torch. It will relinquish the flame once defeated.", "note": "\"Dance and die and live forever,<br>Silent voices shout and sing,<br>Stand before the Troupe's dark heart,<br>Burn away the Nightmare King.\"<br>- 'The Grimm Troupe'", "icon": "bestiary__0002_grimmkin_tall_portrait.png", "preview": "Bestiary_Grimmkin_tall.png", "id": "FLAMEBEARER_LARGE"},
  {"name": "Grimm", "desc": "Master of the Grimm Troupe.", "note": "\"Through dream I travel, at lantern's call,<br>To consume the flames of a kingdom's fall.\"<br>- Grimm", "icon": "bestiary__0001_grimm_boss_portrait.png", "preview": "Bestiary_Grimm_boss.png", "id": "GRIMM"},
  {"name": "Nightmare King", "desc": "Twisted spectre of scarlet flame.", "note": "\"The expanse of dream in past was split,<br>One realm now must stay apart,<br>Darkest reaches, beating red,<br>Terror of sleep. The Nightmare's Heart.\"<br>- Seer", "icon": "bestiary__0000_grimm_nightmare_boss_portrait.png", "preview": "Bestiary_Grimm_Nightmare_boss.png", "id": "NIGHTMARE_GRIMM"},
  {"name": "Nailmasters Oro & Mato", "desc": "Brothers of the nail, trained in combat by the Great Nailsage.", "note": "\"Gods by toil and nail bound,<br>Brothers sworn to guard the weak,<br>Masters of the sacred ground,<br>Help Us find the God We seek!\"<br>- Prayer to the Masters", "icon": "bestiary_gg__0003_icon_nailmasters.png", "preview": "bestiary_gg__0006_nailmasters.png", "id": "NAILMASTERS"},
  {"name": "Paintmaster Sheo", "desc": "Skilled and respected master of the nail. Always eager to learn new things.", "note": "\"O God inspired, master of arts,<br>Whose works shall eternal endure,<br>Peer beyond Our minds and hearts,<br>Reveal to Us the God most pure!\"<br>- Prayer to the Artist", "icon": "bestiary_gg__0002_icon_paintmaster.png", "preview": "bestiary_gg__0007_paintmaster.png", "id": "PAINTMASTER"},
  {"name": "Great Nailsage Sly", "desc": "Legendary sage of the nail arts and, recently, merchant of some success.", "note": "\"Sagely God of the cunning and bold,<br>Sharpen Our nails and show Us the odds,<br>O greatest of masters, We wish to behold,<br>That one still greater, the God of Gods!\"<br>- Prayer to the Sage", "icon": "bestiary_gg__0004_icon_sage.png", "preview": "bestiary_gg__0005_sage.png", "id": "SAGE_SLY"},
  {"name": "Wingmould", "desc": "A fly-like construct formed of white shell and a dark liquid interior.", "note": "The bugs of Hallownest believed that their King created this world and everything in it. For what purpose, I wonder? Were his subjects companions, or toys, or children? Such a mind seems unknowable.", "icon": "bestiary__0042_white-palace-fly_s.png", "preview": "bestiary__0043_white-palace-fly.png", "id": "PALACE_FLY"},
  {"name": "Royal Retainer", "desc": "The most loyal and devout servants of the King.", "note": "Simple, weak creatures find strength by forming a hive and obeying a ruler. Such creatures become the arms and teeth and claws of their monarch.", "icon": "bestiary_white_servant_s.png", "preview": "bestiary_white_servant_f.png", "id": "WHITE_ROYAL"},
  {"name": "Kingsmould", "desc": "Guard construct of Hallownest's Palace and skilled wielder of the curved claw-blade. Its heavy white armour protects a fragile body.", "note": "They say the old King died long ago, but his influence echoes around us still. A creature like that... what did he desire?", "icon": "bestiary_kingsguard_s.png", "preview": "bestiary_kingsguard_f.png", "id": "ROYAL_GUARD"},
  {"name": "Sibling", "desc": "Fragment of a lingering will.", "note": "Far down below us, beneath the kingdom, the air grows stiller and a sense of emptiness pervades. Can life flourish down there? If so, I will hunt it.", "icon": "bestiary__0044_shade-sibling_s.png", "preview": "bestiary__0045_shade-sibling.png", "id": "SIBLING"},
  {"name": "Void Tendrils", "desc": "Fragments of void taking the shape of sharp, thrashing tendrils.", "note": "The bugs of Hallownest sometimes wondered whether there were other, older, stranger Kingdoms deep below them. What strange fancies they had. That's probably why they were always so distracted and easy to kill.", "icon": "bestiary_abyss_tendrils_s.png", "preview": "bestiary_abyss_tendrils_f.png", "id": "ABYSS_TENDRIL"},
  {"name": "Hornet", "desc": "Skilled protector of Hallownest's ruins. Wields a needle and thread.", "note": "I have seen this nimble little creature. I thought her prey and pounced at her, but with a flash she stabbed me with her flying stinger and darted away. Could she be... a Hunter?", "icon": "bestiary_hornet_s.png", "preview": "bestiary_hornet.png", "id": "HORNET"},
  {"name": "Hollow Knight", "desc": "Fully grown Vessel, carrying the plague's heart within its body.", "note": "The old King of Hallownest... he must have been desperate to save his crumbling little world. The sacrifices he imposed on others... all for nothing.", "icon": "bestiary__0002_hollow-knight_s.png", "preview": "bestiary__0003_hollow-knight.png", "id": "HOLLOW_KNIGHT"},
  {"name": "Radiance", "desc": "The light, forgotten.", "note": "The plague, the infection, the madness that haunts the corpses of Hallownest... the light that screams out from the eyes of this dead Kingdom. What is the source? I suppose mere mortals like myself will never understand.", "icon": "bestiary__0000_radiant_moth_s.png", "preview": "bestiary__0001_radiant_moth.png", "id": "FINAL_BOSS"},
  {"name": "Shade", "desc": "Echo of a previous life. Defeat it to retake its power and become whole.", "note": "Each of us leaves an imprint of something when we die. A stain on the world. I don't know how much longer this kingdom can bear the weight of so many past lives...", "icon": "bestiary_hollow-shade_s.png", "preview": "bestiary_hollow-shade_f.png", "id": "HOLLOW_SHADE"},
  {"name": "Hunter's Mark", "desc": "The mark of a true Hunter.", "note": "I have no offspring, nor subjects, nor worshippers. The sum of my being, my learning, my instincts... I leave it all to you. Good luck, Hunter.", "icon": "bestiary_hunter_mark.png", "preview": "bestiary_hunter_mark_f.png", "id": "HUNTERMARK"},
  {"name": "Weathered Mask", "desc": "Strange mask from a godless land, passed down over time. The design suggests the wearer's thoughts were focused through the crest.", "note": "\"Gods of Thunder, Gods of Rain! Why forsake thy servants? Will Our minds be left suffering, to ache alone? What God remains to deliver Us from this woeful silence?\"<br>- Lament of the Godseekers", "icon": "bestiary_gg_mask_icon.png", "preview": "bestiary_gg_mask.png", "id": "GODSEEKER_MASK"},
  {"name": "Void Idol", "desc": "Rare artifact from a time before Hallownest. Contains a drop of liquid from the Abyss.", "note": "\"Inspired or mad, those ancient bugs. They devoted their worship to no lord, or power, or strength, but to the very darkness itself.\"<br>- Lemm", "icon": "bestiary_gg_shade_idol_icon.png", "preview": "bestiary_gg_void_idol.png", "id": "VOID_IDOL_1"},
  {"name": "Pure Vessel", "desc": "Chosen vessel, raised and trained to prime form.", "note": "\"Deepest silence in holy shell,<br>Given nail and named a Knight,<br>Bound by chain and egg and spell,<br>Hear Our plea! Reveal thy Light!\"<br>- Prayer to the Vessel", "icon": "bestiary_gg__0001_icon_HK_prime.png", "preview": "bestiary_gg__0008_HK_prime.png", "id": "PURE_VESSEL"},
  {"name": "Seal of Binding", "desc": "Intricate seal of ancient design.<br><br>Used to contain a powerful force, or to preserve something of great importance.", "note": "", "icon": "bestiary_vessel_seal.png", "preview": "vessel_seal.png", "id": "KIN_SEAL"}
]
